import React, { useState } from "react";
import SettingsDialog from "./SettingsDialog"; // Placeholder for the actual dialog component

const SettingsButton = () => {
  const [isDialogOpen, setIsDialogOpen] = useState(false);

  const styles = {
    button: {
      width: "40px",
      height: "40px",
      borderRadius: "5px",
      backgroundColor: "white",
      cursor: "pointer",
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
    },
    icon: {
      width: "20px",
      height: "20px",
    },
  };

  return (
    <div>
      <button style={styles.button} onClick={() => setIsDialogOpen(true)}>
        <img src="settings.png" alt="Settings" style={styles.icon} />
      </button>
      {isDialogOpen && <SettingsDialog onClose={() => setIsDialogOpen(false)} />}
    </div>
  );
};

export default SettingsButton;
