import React, { useState } from "react";
import MenuBar from "../MenuBar/MenuBar";
import SequenceWidget from "../SequenceWidget/SequenceWidget";
import OptionPicker from "../OptionPicker/OptionPicker";
import SnowfallBackground from "../Backgrounds/SnowfallBackground";

const MainWidget = () => {
  const [background, setBackground] = useState("Snowfall");

  const styles = {
    app: {
      display: "flex",
      flexDirection: "column",
      height: "100vh", // Full viewport height
      width: "100vw", // Full viewport width
      position: "relative", // Ensure background positioning works
    },
    menuBar: {
      flex: "0 0 auto", // MenuBar occupies a fixed height
      zIndex: 1, // Ensure it stays above the background
    },
    mainContent: {
      flex: 1, // Fills remaining height
      display: "flex", // Horizontal layout
      flexDirection: "row",
      overflow: "hidden", // Avoid layout spilling
    },
    sequenceWidget: {
      flex: 1, // Takes more space compared to OptionPicker
      borderRight: "1px solid rgba(255, 255, 255, 0.2)", // Optional separator
      overflow: "hidden", // Avoid scrollbars
    },
    optionPicker: {
      flex: 1, // Smaller section
      overflow: "hidden", // Avoid scrollbars
    },
  };

  return (
    <div id="app" style={styles.app}>
      <SnowfallBackground background={background} />
      <div style={styles.menuBar}>
        <MenuBar onBackgroundChange={setBackground} />
      </div>
      <div style={styles.mainContent}>
        <div style={styles.sequenceWidget}>
          <SequenceWidget />
        </div>
        <div style={styles.optionPicker}>
          <OptionPicker />
        </div>
      </div>
    </div>
  );
};

export default MainWidget;
