import React from "react";

const DirectSetTurnsDialog = ({ currentTurns, onSelectTurns, onClose }) => {
  const turnsValues = ["fl", "0", "0.5", "1", "1.5", "2", "2.5", "3"];

  const styles = {
    dialog: {
      position: "absolute",
      border: "2px solid #2e3192",
      borderRadius: "10px",
      backgroundColor: "#f9f9f9",
      display: "grid",
      gridTemplateColumns: "repeat(4, 1fr)", // Ensure a grid layout
      gap: "10px",
      padding: "15px",
      boxShadow: "0px 4px 8px rgba(0, 0, 0, 0.2)", // Add some depth
      fontSize: "18px",
    },
    button: {
      border: "2px solid #ccc",
      borderRadius: "8px",
      padding: "10px 20px",
      backgroundColor: "white",
      cursor: "pointer",
      fontSize: "16px",
      transition: "background-color 0.3s, transform 0.2s, box-shadow 0.2s",
    },
    buttonHover: {
      backgroundColor: "#e0e7ff", // Subtle hover color
      boxShadow: "0px 4px 8px rgba(0, 0, 0, 0.1)", // Elevate on hover
      transform: "scale(1.05)", // Slightly larger
    },
    buttonActive: {
      backgroundColor: "#c7d2fe",
      boxShadow: "inset 0px 4px 8px rgba(0, 0, 0, 0.2)", // Depressed look
    },
  };

  return (
    <div style={styles.dialog}>
      {turnsValues.map((value) => (
        <button
          key={value}
          style={styles.button}
          onMouseOver={(e) =>
            (e.currentTarget.style = {
              ...styles.button,
              ...styles.buttonHover,
            })
          }
          onMouseOut={(e) =>
            (e.currentTarget.style = { ...styles.button })
          }
          onMouseDown={(e) =>
            (e.currentTarget.style = {
              ...styles.button,
              ...styles.buttonActive,
            })
          }
          onMouseUp={(e) =>
            (e.currentTarget.style = {
              ...styles.button,
              ...styles.buttonHover,
            })
          }
          onClick={() => {
            onSelectTurns(value);
            onClose();
          }}
        >
          {value}
        </button>
      ))}
    </div>
  );
};

export default DirectSetTurnsDialog;
