import React, { useEffect, useState } from "react";

const SequenceWidgetButtonPanel = ({
  onAddToDictionary,
  onSaveImage,
  onViewFullScreen,
  onMirrorSequence,
  onSwapColors,
  onRotateSequence,
  onDeleteBeat,
  onClearSequence,
}) => {
  const [buttonSize, setButtonSize] = useState(50); // Default size
  const [hoveredButton, setHoveredButton] = useState(null); // Track hovered button

  useEffect(() => {
    // Adjust button size based on the container size
    const updateButtonSize = () => {
      const containerHeight = window.innerHeight; // Or use a ref to the parent container
      const newSize = Math.max(40, Math.min(80, containerHeight / 20)); // Adjust scaling logic
      setButtonSize(newSize);
    };

    updateButtonSize(); // Set initial size
    window.addEventListener("resize", updateButtonSize); // Update on resize

    return () => {
      window.removeEventListener("resize", updateButtonSize); // Cleanup
    };
  }, []);

  const buttonPanelStyles = {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    gap: `${buttonSize * 0.2}px`, // Spacing scales with button size
    padding: "10px",
    backgroundColor: "transparent",
    height: "100%",
    width: "100%",
  };

  const buttonStyles = (isHovered) => ({
    width: `${buttonSize}px`,
    height: `${buttonSize}px`,
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    backgroundColor: isHovered ? "#f0f0f0" : "white",
    borderRadius: "50%",
    cursor: "pointer",
    transition: "background-color 0.2s, transform 0.2s",
    border: "1px solid #ccc",
    fontSize: `${buttonSize * 0.5}px`, // Adjust icon size
    transform: isHovered ? "scale(1.1)" : "scale(1)",
    ':active': {
      transform: "scale(0.9)",
    },
  });

  return (
    <div style={buttonPanelStyles}>
      {/* Group 1: Basic Tools */}
      <button
        style={buttonStyles(hoveredButton === "addToDictionary")}
        onClick={onAddToDictionary}
        title="Add to Dictionary"
        onMouseEnter={() => setHoveredButton("addToDictionary")}
        onMouseLeave={() => setHoveredButton(null)}
      >
        📖
      </button>
      <button
        style={buttonStyles(hoveredButton === "saveImage")}
        onClick={onSaveImage}
        title="Save Image"
        onMouseEnter={() => setHoveredButton("saveImage")}
        onMouseLeave={() => setHoveredButton(null)}
      >
        💾
      </button>
      <button
        style={buttonStyles(hoveredButton === "viewFullScreen")}
        onClick={onViewFullScreen}
        title="View Full Screen"
        onMouseEnter={() => setHoveredButton("viewFullScreen")}
        onMouseLeave={() => setHoveredButton(null)}
      >
        👁️
      </button>

      {/* Spacer */}
      <div style={{ height: `${buttonSize * 0.5}px` }}></div>

      {/* Group 2: Transform Tools */}
      <button
        style={buttonStyles(hoveredButton === "mirrorSequence")}
        onClick={onMirrorSequence}
        title="Mirror Sequence"
        onMouseEnter={() => setHoveredButton("mirrorSequence")}
        onMouseLeave={() => setHoveredButton(null)}
      >
        🔄
      </button>
      <button
        style={buttonStyles(hoveredButton === "swapColors")}
        onClick={onSwapColors}
        title="Swap Colors"
        onMouseEnter={() => setHoveredButton("swapColors")}
        onMouseLeave={() => setHoveredButton(null)}
      >
        ⚫⚪
      </button>
      <button
        style={buttonStyles(hoveredButton === "rotateSequence")}
        onClick={onRotateSequence}
        title="Rotate Sequence"
        onMouseEnter={() => setHoveredButton("rotateSequence")}
        onMouseLeave={() => setHoveredButton(null)}
      >
        ⟳
      </button>

      {/* Spacer */}
      <div style={{ height: `${buttonSize * 0.5}px` }}></div>

      {/* Group 3: Sequence Management */}
      <button
        style={buttonStyles(hoveredButton === "deleteBeat")}
        onClick={onDeleteBeat}
        title="Delete Beat"
        onMouseEnter={() => setHoveredButton("deleteBeat")}
        onMouseLeave={() => setHoveredButton(null)}
      >
        🗑️
      </button>
      <button
        style={buttonStyles(hoveredButton === "clearSequence")}
        onClick={onClearSequence}
        title="Clear Sequence"
        onMouseEnter={() => setHoveredButton("clearSequence")}
        onMouseLeave={() => setHoveredButton(null)}
      >
        ❌
      </button>
    </div>
  );
};

export default SequenceWidgetButtonPanel;
