import React from "react";

const TurnsBoxHeader = ({
  color,
  onCWClick,
  onCCWClick,
  isCWPressed,
  isCCWPressed,
}) => {
  const headerStyles = {
    container: {
      display: "flex",
      justifyContent: "space-between",
      alignItems: "center",
      padding: "10px",
      backgroundColor: "white",
      borderBottom: `2px solid ${
        color === "blue" ? "#2e3192" : "#ed1c24"
      }`,
    },
    button: {
      width: "50px",
      height: "50px",
      borderRadius: "25px",
      border: "2px solid",
      backgroundColor: "white",
      cursor: "pointer",
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
      transition: "background-color 0.3s, transform 0.2s",
    },
    buttonPressed: {
      backgroundColor: "#ccd9ff",
      // border: `2px solid ${
      //   color === "blue" ? "#2e3192" : "#ed1c24"
      // }`,
      transform: "scale(0.95)",
    },
    headerLabel: {
      fontSize: "1.5rem",
      fontWeight: "bold",
      color: color === "blue" ? "#2e3192" : "#ed1c24",
    },
  };

  return (
    <div style={headerStyles.container}>
      <button
        style={{
          ...headerStyles.button,
          ...(isCCWPressed ? headerStyles.buttonPressed : {}),
        }}
        onClick={onCCWClick}
      >
        ⟲
      </button>
      <span style={headerStyles.headerLabel}>
        {color === "blue" ? "Left" : "Right"}
      </span>
      <button
        style={{
          ...headerStyles.button,
          ...(isCWPressed ? headerStyles.buttonPressed : {}),
        }}
        onClick={onCWClick}
      >
        ⟳
      </button>
    </div>
  );
};

export default TurnsBoxHeader;
