import React from "react";

const GraphEditorPictographContainer = () => {
  const styles = {
    pictographContainer: {
      aspectRatio: "1 / 1",
      width: "100%", // Ensures it scales responsively
      height: "auto", // Maintains aspect ratio
      border: "1px solid #ddd",
      backgroundColor: "#fff",
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
      fontSize: "1.2rem",
      fontWeight: "bold",
    },
  };

  return (
    <div style={styles.pictographContainer}>
      <p>Pictograph Placeholder</p>
    </div>
  );
};

export default GraphEditorPictographContainer;
