import React, { useState, useEffect } from "react";

const Button = ({ icon, title, onClick, buttonSize }) => {
  const [ripples, setRipples] = useState([]);

  useEffect(() => {
    // Inject keyframes for ripple animation into the DOM
    const styleSheet = document.styleSheets[0];
    const rippleKeyframes = `
      @keyframes ripple {
        from {
          width: 0px;
          height: 0px;
          opacity: 0.5;
        }
        to {
          width: 200px;
          height: 200px;
          opacity: 0;
        }
      }
    `;

    // Check if keyframes already exist to prevent duplicates
    if (!Array.from(styleSheet.cssRules).find((rule) => rule.name === "ripple")) {
      styleSheet.insertRule(rippleKeyframes, styleSheet.cssRules.length);
    }
  }, []);

  const handleClick = (e) => {
    const rect = e.currentTarget.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;
    const newRipple = { x, y, key: Date.now() };
    setRipples([...ripples, newRipple]);
    setTimeout(() => setRipples((prev) => prev.filter((r) => r.key !== newRipple.key)), 500);
    onClick();
  };

  const buttonStyles = {
    position: "relative",
    width: `${buttonSize}px`,
    height: `${buttonSize}px`,
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    backgroundColor: "white",
    borderRadius: "50%",
    cursor: "pointer",
    overflow: "hidden", // Ensure ripple stays within the button
    border: "1px solid #ccc",
    boxShadow: "0px 2px 4px rgba(0, 0, 0, 0.1)",
  };

  const rippleStyles = (x, y) => ({
    position: "absolute",
    top: `${y}px`,
    left: `${x}px`,
    width: "0px",
    height: "0px",
    borderRadius: "50%",
    backgroundColor: "rgba(0, 123, 255, 0.5)", // Blue ripple color
    animation: "ripple 0.5s linear",
  });

  return (
    <button style={buttonStyles} onClick={handleClick}>
      {ripples.map((ripple) => (
        <span key={ripple.key} style={rippleStyles(ripple.x, ripple.y)}></span>
      ))}
      <img src={icon} alt={title} style={{ width: "70%", height: "70%", objectFit: "contain" }} />
    </button>
  );
};

export default Button;
