import React from "react";
import SocialMediaWidget from "./SocialMediaWidget";
import NavigationWidget from "./NavigationWidget";
import SettingsButton from "./SettingsButton";

const MenuBar = () => {
  const styles = {
    menuBar: {
      display: "flex",
      justifyContent: "space-between",
      alignItems: "center",
      backgroundColor: "#333",
      color: "white",
      padding: "10px",
      width: "100%",
      zIndex: 10, // Ensure it stays above other content
    },
    menuSection: {
      flex: 1,
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
    },
    leftSection: {
      justifyContent: "flex-start",
    },
    rightSection: {
      justifyContent: "flex-end",
    },
  };

  return (
    <header style={styles.menuBar}>
      {/* Social Media Widget */}
      <div style={{ ...styles.menuSection, ...styles.leftSection }}>
        <SocialMediaWidget />
      </div>

      {/* Navigation Widget */}
      <div style={styles.menuSection}>
        <NavigationWidget />
      </div>

      {/* Settings Button */}
      <div style={{ ...styles.menuSection, ...styles.rightSection }}>
        <SettingsButton />
      </div>
    </header>
  );
};

export default MenuBar;
