import React from "react";

// Styles defined directly in the component file
const turnsWidgetStyles = {
  turnsWidget: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    gap: "10px",
    width: "100%",
  },
  turnsTextLabel: {
    fontSize: "1.2rem",
    fontWeight: "bold",
    textDecoration: "underline",
  },
  motionTypeLabel: {
    fontSize: "1.2rem",
    fontWeight: "bold",
    textAlign: "center",
  },
};

const TurnsWidget = ({ color }) => {
  return (
    <div style={turnsWidgetStyles.turnsWidget}>
      <div style={turnsWidgetStyles.turnsTextLabel}>Turns</div>
      <div style={turnsWidgetStyles.motionTypeLabel}>Motion Type</div>
    </div>
  );
};

export default TurnsWidget;
