import React, { useEffect, useState } from "react";
import GraphEditorPictographContainer from "./GraphEditorPictographContainer";
import TurnsBox from "./TurnsBox/TurnsBox";

const GraphEditor = ({ isExpanded }) => {
  const [isFullyCollapsed, setIsFullyCollapsed] = useState(false);

  useEffect(() => {
    if (!isExpanded) {
      const timer = setTimeout(() => setIsFullyCollapsed(true), 300); // Match animation duration
      return () => clearTimeout(timer);
    } else {
      setIsFullyCollapsed(false);
    }
  }, [isExpanded]);

  // Styles specific to GraphEditor
  const styles = {
    graphEditor: {
      position: "relative",
      backgroundColor: "#f4f4f4",
      borderTop: "2px solid #ccc",
      overflow: "hidden",
      width: "100%",
      height: isExpanded ? "300px" : "0px", // Adjust height dynamically
      opacity: isFullyCollapsed ? 0 : 1, // Hide when collapsed
      transition: "height 0.3s ease-in-out, opacity 0.3s ease-in-out", // Animation styles
    },
    mainLayout: {
      display: "flex",
      flexDirection: "row",
      height: "100%",
    },
    turnsBox: {
      flex: 1, // Allow the turns boxes to flexibly take up remaining space
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
    },
    pictographWrapper: {
      flex: 0,
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
      height: "100%", // Take the full height of the parent
    },
    pictographContainer: {
      aspectRatio: "1 / 1", // Maintain a 1:1 aspect ratio
      width: "100%", // Ensure it scales properly within the wrapper
      height: "auto", // Automatically adjust height to maintain the ratio
      border: "1px solid #ddd", // Optional styling
      backgroundColor: "#fff",
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
    },
  };

  return (
    <div style={styles.graphEditor}>
      <div style={styles.mainLayout}>
        <div style={styles.turnsBox}>
          <TurnsBox color="blue" label="Left" />
        </div>
        <div style={styles.pictographWrapper}>
          <div style={styles.pictographContainer}>
            <GraphEditorPictographContainer />
          </div>
        </div>
        <div style={styles.turnsBox}>
          <TurnsBox color="red" label="Right" />
        </div>
      </div>
    </div>
  );
};

export default GraphEditor;
