import React from "react";

const styles = {
  toggleTab: {
    position: "absolute",
    bottom: 0,
    left: 0,
    width: "20%",
    textAlign: "center",
    padding: "5px",
    backgroundColor: "#ccc",
    cursor: "pointer",
    fontSize: "1rem",
    border: "1px solid #aaa",
    transition: "background-color 0.3s ease-in-out",
  },
  toggleTabHover: {
    backgroundColor: "#ddd",
  },
};

class GraphEditorToggleTab extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isHovered: false,
    };
  }

  handleMouseEnter = () => {
    this.setState({ isHovered: true });
  };

  handleMouseLeave = () => {
    this.setState({ isHovered: false });
  };

  render() {
    const { onToggle, isExpanded, position } = this.props;
    const { isHovered } = this.state;

    return (
      <div
        onClick={onToggle}
        onMouseEnter={this.handleMouseEnter}
        onMouseLeave={this.handleMouseLeave}
        style={{
          ...styles.toggleTab,
          ...(isHovered ? styles.toggleTabHover : {}),
          transform: `translateY(${position}px)`, // Dynamically position tab
        }}
      >
        <p>{isExpanded ? "Collapse Editor" : "Expand Editor"}</p>
      </div>
    );
  }
}

export default GraphEditorToggleTab;
