import React from "react";
import ReactDOM from "react-dom/client";
import "./MainAppStyles.css";
import SequenceWidget from "../SequenceWidget/SequenceWidget";
import OptionPicker from "../OptionPicker/OptionPicker";
import SnowfallBackground from "../Backgrounds/SnowfallBackground"; // Import SnowfallBackground

const MainApp = () => {
  return (
    <div id="app">
      {/* Add the snowfall background */}
      <SnowfallBackground />

      {/* Wrap the main content in a container to layer it above the background */}
      <div className="main-content">
        <header className="header">
          <div className="social-media-buttons">Social Media</div>
          <div className="navigation-buttons">Navigation</div>
          <div className="options-button">Options</div>
        </header>
        <main className="main-layout">
          <SequenceWidget />
          <OptionPicker />
        </main>
      </div>
    </div>
  );
};

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(<MainApp />);
