import React, { useState } from "react";
import IncrementButton from "./IncrementButton";
import DirectSetTurnsDialog from "../DirectSetTurnsDialog/DirectSetTurnsDialog";

const styles = {
  turnsDisplayFrame: {
    display: "flex",
    alignItems: "center",
    justifyContent: "space-between", // Ensure buttons are spaced evenly
    width: "100%", // Stretch to full width of the parent container
    flex: 1, // Allow it to take vertical space if needed
  },
  turnsLabel: {
    fontSize: "3rem",
    fontWeight: "bold",
    textAlign: "center",
    width: "100px",
    height: "100px",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    borderRadius: "50px",
    borderWidth: "4px",
    borderStyle: "solid",
    backgroundColor: "white",
    color: "black",
    cursor: "pointer",
  },
  blue: {
    borderColor: "#2e3192",
  },
  red: {
    borderColor: "#ed1c24",
  },
};

const TurnsLabel = ({ turns, color, onClick }) => {
  return (
    <div
      style={{
        ...styles.turnsLabel,
        ...(color === "blue" ? styles.blue : styles.red),
      }}
      onClick={onClick}
    >
      {turns}
    </div>
  );
};

export default TurnsLabel;
