import React, { useEffect, useState } from "react";

const Button = ({ icon, title, onClick, isHovered, isClicked, onMouseEnter, onMouseLeave, onMouseDown, onMouseUp, buttonSize }) => {
  const buttonStyles = {
    width: `${buttonSize}px`,
    height: `${buttonSize}px`,
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    backgroundColor: isHovered ? "#f0f0f0" : "white",
    borderRadius: "50%",
    cursor: "pointer",
    transition: "background-color 0.2s, transform 0.2s",
    border: "1px solid #ccc",
    fontSize: `${buttonSize * 0.5}px`, // Adjust icon size
    transform: isClicked ? "scale(0.9)" : isHovered ? "scale(1.1)" : "scale(1)",
  };

  return (
    <button
      style={buttonStyles}
      onClick={onClick}
      title={title}
      onMouseEnter={onMouseEnter}
      onMouseLeave={onMouseLeave}
      onMouseDown={onMouseDown}
      onMouseUp={onMouseUp}
    >
      {icon}
    </button>
  );
};

const SequenceWidgetButtonPanel = ({
  onAddToDictionary,
  onSaveImage,
  onViewFullScreen,
  onMirrorSequence,
  onSwapColors,
  onRotateSequence,
  onDeleteBeat,
  onClearSequence,
}) => {
  const [buttonSize, setButtonSize] = useState(50); // Default size
  const [hoveredButton, setHoveredButton] = useState(null); // Track hovered button
  const [clickedButton, setClickedButton] = useState(null); // Track clicked button

  useEffect(() => {
    // Adjust button size based on the container size
    const updateButtonSize = () => {
      const containerHeight = window.innerHeight; // Or use a ref to the parent container
      const newSize = Math.max(40, Math.min(80, containerHeight / 20)); // Adjust scaling logic
      setButtonSize(newSize);
    };

    updateButtonSize(); // Set initial size
    window.addEventListener("resize", updateButtonSize); // Update on resize

    return () => {
      window.removeEventListener("resize", updateButtonSize); // Cleanup
    };
  }, []);

  const buttonPanelStyles = {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    gap: `${buttonSize * 0.2}px`, // Spacing scales with button size
    padding: "10px",
    backgroundColor: "transparent",
    height: "100%",
    width: "100%",
  };

  const buttons = [
    { icon: "üìñ", title: "Add to Dictionary", onClick: onAddToDictionary, id: "addToDictionary" },
    { icon: "üíæ", title: "Save Image", onClick: onSaveImage, id: "saveImage" },
    { icon: "üëÅÔ∏è", title: "View Full Screen", onClick: onViewFullScreen, id: "viewFullScreen" },
    { icon: "üîÑ", title: "Mirror Sequence", onClick: onMirrorSequence, id: "mirrorSequence" },
    { icon: "‚ö´‚ö™", title: "Swap Colors", onClick: onSwapColors, id: "swapColors" },
    { icon: "‚ü≥", title: "Rotate Sequence", onClick: onRotateSequence, id: "rotateSequence" },
    { icon: "üóëÔ∏è", title: "Delete Beat", onClick: onDeleteBeat, id: "deleteBeat" },
    { icon: "‚ùå", title: "Clear Sequence", onClick: onClearSequence, id: "clearSequence" },
  ];

  return (
    <div style={buttonPanelStyles}>
      {buttons.map((button, index) => (
        <React.Fragment key={button.id}>
          {index === 3 || index === 6 ? <div style={{ height: `${buttonSize * 0.5}px` }}></div> : null}
          <Button
            icon={button.icon}
            title={button.title}
            onClick={button.onClick}
            isHovered={hoveredButton === button.id}
            isClicked={clickedButton === button.id}
            onMouseEnter={() => setHoveredButton(button.id)}
            onMouseLeave={() => setHoveredButton(null)}
            onMouseDown={() => setClickedButton(button.id)}
            onMouseUp={() => setClickedButton(null)}
            buttonSize={buttonSize}
          />
        </React.Fragment>
      ))}
    </div>
  );
};

export default SequenceWidgetButtonPanel;
