import React, { useState, useEffect } from "react";

const NavigationWidget = ({ onTabChange }) => {
  const [activeTab, setActiveTab] = useState(0);
  const tabNames = [
    "Construct âš’ï¸",
    "Generate ðŸ¤–",
    "Browse ðŸ”",
    "Learn ðŸ§ ",
    "Write âœï¸",
  ];
  const [buttonStyles, setButtonStyles] = useState({});

  const updateButtonStyles = () => {
    const windowWidth = window.innerWidth;
    const windowHeight = window.innerHeight;

    const fontSize = Math.max(10, windowWidth / 120);
    const buttonWidth = Math.max(80, windowWidth / 10);
    const buttonHeight = Math.max(30, windowHeight / 22);

    const styles = {
      base: {
        fontSize: `${fontSize}px`,
        fontFamily: "Georgia, serif",
        padding: "5px 10px",
        borderRadius: "5px",
        border: "1px solid gray",
        cursor: "pointer",
        transition: "all 0.3s ease",
      },
      active: {
        backgroundColor: "blue",
        color: "white",
        fontWeight: "bold",
      },
      inactive: {
        backgroundColor: "white",
        color: "black",
      },
      dimensions: {
        width: `${buttonWidth}px`,
        height: `${buttonHeight}px`,
      },
    };

    setButtonStyles(styles);
  };

  useEffect(() => {
    updateButtonStyles();
    window.addEventListener("resize", updateButtonStyles);

    return () => {
      window.removeEventListener("resize", updateButtonStyles);
    };
  }, []);

  const handleTabClick = (index) => {
    setActiveTab(index);
    if (onTabChange) {
      onTabChange(index);
    }
  };

  return (
    <div style={{ display: "flex", justifyContent: "center", gap: "10px" }}>
      {tabNames.map((name, index) => (
        <button
          key={index}
          onClick={() => handleTabClick(index)}
          style={{
            ...buttonStyles.base,
            ...buttonStyles.dimensions,
            ...(index === activeTab
              ? buttonStyles.active
              : buttonStyles.inactive),
          }}
        >
          {name}
        </button>
      ))}
    </div>
  );
};

export default NavigationWidget;
