const Button = ({
  icon,
  title,
  onClick,
  isHovered,
  isClicked,
  onMouseEnter,
  onMouseLeave,
  onMouseDown,
  onMouseUp,
  buttonSize,
}) => {
  const buttonStyles = {
    width: `${buttonSize}px`,
    height: `${buttonSize}px`,
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    backgroundColor: isHovered ? "#f0f0f0" : "white",
    borderRadius: "50%",
    cursor: "pointer",
    transition: "box-shadow 0.2s",
    border: "1px solid #ccc",
    boxShadow: isClicked
      ? "0px 0px 10px rgba(0, 123, 255, 0.7)" // Glowing blue effect
      : "0px 2px 4px rgba(0, 0, 0, 0.1)",
  };
  

  const iconStyles = {
    width: "70%", // Maintain aspect ratio inside button
    height: "70%",
    objectFit: "contain",
  };

  return (
    <button
      style={buttonStyles}
      onClick={onClick}
      title={title}
      onMouseEnter={onMouseEnter}
      onMouseLeave={onMouseLeave}
      onMouseDown={onMouseDown}
      onMouseUp={onMouseUp}
    >
      <img src={icon} alt={title} style={iconStyles} />
    </button>
  );
};

export default Button;
