import React from "react";

const SocialMediaWidget = () => {
  const styles = {
    container: {
      display: "grid",
      gridTemplateColumns: "repeat(3, auto)",
      gap: "10px",
    },
    button: {
      width: "40px",
      height: "40px",
      borderRadius: "5px",
      backgroundColor: "white",
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
      cursor: "pointer",
      transition: "background-color 0.2s ease-in-out",
    },
    buttonHover: {
      backgroundColor: "#e6e6e6",
    },
    icon: {
      width: "20px",
      height: "20px",
    },
  };

  const socialLinks = [
    { icon: "paypal.png", link: "https://www.paypal.me/austencloud" },
    { icon: "venmo.png", link: "https://venmo.com/austencloud" },
    { icon: "github.png", link: "https://github.com/austencloud/the-kinetic-constructor" },
    { icon: "facebook.png", link: "https://www.facebook.com/thekineticalphabet" },
    { icon: "instagram.png", link: "https://www.instagram.com/thekineticalphabet" },
    { icon: "youtube.png", link: "https://www.youtube.com/channel/UCbLHNRSASZS_gwkmRATH1-A" },
  ];

  return (
    <div style={styles.container}>
      {socialLinks.map((item, index) => (
        <a
          key={index}
          href={item.link}
          target="_blank"
          rel="noopener noreferrer"
          style={styles.button}
          onMouseEnter={(e) => (e.currentTarget.style.backgroundColor = styles.buttonHover.backgroundColor)}
          onMouseLeave={(e) => (e.currentTarget.style.backgroundColor = styles.button.backgroundColor)}
        >
          <img src={item.icon} alt="" style={styles.icon} />
        </a>
      ))}
    </div>
  );
};

export default SocialMediaWidget;
