import React, { useEffect, useState } from "react";

const GraphEditorToggleTab = ({ onToggle, isExpanded, animationDuration, editorHeight }) => {
  const [currentBottom, setCurrentBottom] = useState("0px");

  useEffect(() => {
    // Update the `bottom` property dynamically
    const height = isExpanded ? `${editorHeight}px` : "0px";
    setCurrentBottom(height);
  }, [isExpanded, editorHeight]);

  const styles = {
    toggleTab: {
      position: "absolute",
      bottom: currentBottom,
      left: "0",
      width: "20%",
      textAlign: "center",
      padding: "10px 20px",
      background: "linear-gradient(135deg, #6a11cb, #2575fc)",
      color: "white",
      fontSize: "1rem",
      fontWeight: "bold",
      cursor: "pointer",
      borderRadius: "10px 10px 0 0",
      border: "1px solid #555",
      transition: `bottom ${animationDuration}ms ease-in-out, transform 0.2s ease-in-out`,
      transform: `scale(1)`,
      boxShadow: "0px 4px 8px rgba(0, 0, 0, 0.2)",
    },
    hover: {
      transform: "scale(1.05)",
      boxShadow: "0px 6px 12px rgba(0, 0, 0, 0.3)",
    },
    icon: {
      display: "inline-block",
      transform: isExpanded ? "rotate(180deg)" : "rotate(0deg)",
      transition: `transform ${animationDuration}ms ease-in-out`,
    },
  };

  return (
    <div
      style={styles.toggleTab}
      onClick={onToggle}
      onMouseEnter={(e) => {
        e.currentTarget.style.transform = styles.hover.transform;
        e.currentTarget.style.boxShadow = styles.hover.boxShadow;
      }}
      onMouseLeave={(e) => {
        e.currentTarget.style.transform = styles.toggleTab.transform;
        e.currentTarget.style.boxShadow = styles.toggleTab.boxShadow;
      }}
    >
      <span style={styles.icon}>
        {isExpanded ? "▲" : "▼"}
      </span>{" "}
      {isExpanded ? "Collapse Editor" : "Expand Editor"}
    </div>
  );
};

export default GraphEditorToggleTab;
