// SequenceWidgetButtonPanel.js
import React from "react";

const buttonStyles = {
  buttonPanel: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    gap: "10px",
    padding: "10px",
    borderRadius: "5px",
    backgroundColor: "transparent",
  },
  button: {
    width: "40px",
    height: "40px",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    backgroundColor: "white",
    border: "1px solid #ccc",
    borderRadius: "5px",
    cursor: "pointer",
    transition: "background-color 0.2s",
  },
  buttonHover: {
    backgroundColor: "#e6e6e6",
  },
};

const SequenceWidgetButtonPanel = ({
  onAddToDictionary,
  onSaveImage,
  onViewFullScreen,
  onMirrorSequence,
  onSwapColors,
  onRotateSequence,
  onDeleteBeat,
  onClearSequence,
}) => {
  return (
    <div style={buttonStyles.buttonPanel}>
      {/* Group 1: Basic Tools */}
      <button
        style={buttonStyles.button}
        onClick={onAddToDictionary}
        title="Add to Dictionary"
      >
        üìñ
      </button>
      <button
        style={buttonStyles.button}
        onClick={onSaveImage}
        title="Save Image"
      >
        üíæ
      </button>
      <button
        style={buttonStyles.button}
        onClick={onViewFullScreen}
        title="View Full Screen"
      >
        üëÅÔ∏è
      </button>

      {/* Spacer */}
      <div style={{ height: "20px" }}></div>

      {/* Group 2: Transform Tools */}
      <button
        style={buttonStyles.button}
        onClick={onMirrorSequence}
        title="Mirror Sequence"
      >
        üîÑ
      </button>
      <button
        style={buttonStyles.button}
        onClick={onSwapColors}
        title="Swap Colors"
      >
        ‚ö´‚ö™
      </button>
      <button
        style={buttonStyles.button}
        onClick={onRotateSequence}
        title="Rotate Sequence"
      >
        ‚ü≥
      </button>

      {/* Spacer */}
      <div style={{ height: "20px" }}></div>

      {/* Group 3: Sequence Management */}
      <button
        style={buttonStyles.button}
        onClick={onDeleteBeat}
        title="Delete Beat"
      >
        üóëÔ∏è
      </button>
      <button
        style={buttonStyles.button}
        onClick={onClearSequence}
        title="Clear Sequence"
      >
        ‚ùå
      </button>
    </div>
  );
};

export default SequenceWidgetButtonPanel;
