import React from "react";
import PropRotDirButton from "./PropRotDirButton";
import rotateCWIcon from "./icons/clockwise.png"; // Import icons directly
import rotateCCWIcon from "./icons/counter_clockwise.png";

const TurnsBoxHeader = ({
  color,
  onCWClick,
  onCCWClick,
  isCWPressed,
  isCCWPressed,
}) => {
  const headerStyles = {
    container: {
      display: "flex",
      justifyContent: "space-between",
      alignItems: "center",
      padding: "5px",
      borderBottom: `2px solid ${color === "blue" ? "#2e3192" : "#ed1c24"}`,
    },
    headerLabel: {
      fontSize: "1.5rem",
      fontWeight: "bold",
      color: color === "blue" ? "#2e3192" : "#ed1c24",
      transition: "color 0.3s", // Add transition for color change
    },
    button: {
      cursor: "pointer",
      transition: "transform 0.3s", // Add transition for transform
    },
    buttonHover: {
      transform: "scale(1.1)", // Scale up on hover
    },
    labelHover: {
      color: "#ffcc00", // Change color on hover
    },
  };

  const [isHovered, setIsHovered] = React.useState(false);

  return (
    <div style={headerStyles.container}>
      <div
        style={{ ...headerStyles.button, ...(isHovered ? headerStyles.buttonHover : {}) }}
        onMouseEnter={() => setIsHovered(true)}
        onMouseLeave={() => setIsHovered(false)}
      >
        <PropRotDirButton
          icon={rotateCCWIcon}
          color={color === "blue" ? "#2e3192" : "#ed1c24"}
          onClick={onCCWClick}
          isPressed={isCCWPressed}
        />
      </div>
      <span
        style={{ ...headerStyles.headerLabel, ...(isHovered ? headerStyles.labelHover : {}) }}
        onMouseEnter={() => setIsHovered(true)}
        onMouseLeave={() => setIsHovered(false)}
      >
        {color === "blue" ? "Left" : "Right"}
      </span>
      <div
        style={{ ...headerStyles.button, ...(isHovered ? headerStyles.buttonHover : {}) }}
        onMouseEnter={() => setIsHovered(true)}
        onMouseLeave={() => setIsHovered(false)}
      >
        <PropRotDirButton
          icon={rotateCWIcon}
          color={color === "blue" ? "#2e3192" : "#ed1c24"}
          onClick={onCWClick}
          isPressed={isCWPressed}
        />
      </div>
    </div>
  );
};

export default TurnsBoxHeader;
