import React, { useState } from "react";
import IncrementButton from "./IncrementButton";
import DirectSetTurnsDialog from "../DirectSetTurnsDialog";

const styles = {
  turnsDisplayFrame: {
    display: "flex",
    alignItems: "center",
    justifyContent: "space-between",
    gap: "20px",
    width: "100%",
  },
  turnsLabel: {
    fontSize: "2rem",
    fontWeight: "bold",
    textAlign: "center",
    width: "100px",
    height: "100px",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    borderRadius: "50px",
    borderWidth: "4px",
    borderStyle: "solid", // Ensure the border style is solid
    backgroundColor: "white",
    color: "black", // Ensure text is black
  },
  blue: {
    borderColor: "#2e3192", // Blue border color
  },
  red: {
    borderColor: "#ed1c24", // Red border color
  },
};


const TurnsDisplayFrame = ({ color }) => {
  const [turns, setTurns] = useState(0);
  const [isDialogOpen, setIsDialogOpen] = useState(false);

  const incrementTurns = () => {
    if (turns < 3) setTurns((prev) => parseFloat(prev) + 0.5);
  };

  const decrementTurns = () => {
    if (turns > 0 || turns === "fl") {
      setTurns((prev) => (prev === "fl" ? 0 : parseFloat(prev) - 0.5));
    }
  };

  const handleSelectTurns = (value) => {
    setTurns(value === "fl" ? "fl" : parseFloat(value));
  };

  return (
    <div style={styles.turnsDisplayFrame}>
      <IncrementButton type="decrement" onClick={decrementTurns} />
      <div
        style={{
          ...styles.turnsLabel,
          ...(color === "blue" ? styles.blue : styles.red), // Dynamically set border color
        }}
        onClick={() => setIsDialogOpen(true)}
      >
        {turns}
      </div>
      <IncrementButton type="increment" onClick={incrementTurns} />
      {isDialogOpen && (
        <DirectSetTurnsDialog
          currentTurns={turns}
          onSelectTurns={handleSelectTurns}
          onClose={() => setIsDialogOpen(false)}
        />
      )}
    </div>
  );
};




export default TurnsDisplayFrame;
