import React, { useState } from "react";
import MenuBar from "../MenuBar/MenuBar";
import SequenceWidget from "../SequenceWidget/SequenceWidget";
import OptionPicker from "../OptionPicker/OptionPicker";
import SnowfallBackground from "../Backgrounds/SnowfallBackground";

const MainWidget = () => {
  const [background, setBackground] = useState("Snowfall");

  const styles = {
    app: {
      display: "flex",
      flexDirection: "column",
      height: "100vh", // Full viewport height
      width: "100vw", // Full viewport width
      position: "relative", // Ensure background positioning works
    },
    mainContent: {
      flex: 1,
      display: "flex",
      flexDirection: "column",
      position: "relative",
      overflow: "hidden", // Avoid layout spilling
    },
    canvas: {
      position: "absolute",
      top: 0,
      left: 0,
      width: "100%",
      height: "100%",
      zIndex: 0,
    },
    header: {
      display: "flex",
      justifyContent: "space-between",
      alignItems: "center",
      padding: "10px",
      backgroundColor: "rgba(0, 0, 0, 0.5)",
      color: "white",
    },
    headerDiv: {
      flex: 1,
      textAlign: "center",
    },
    mainLayout: {
      display: "flex",
      flex: 1,
    },
  };

  return (
    <div id="app" style={styles.app}>
      <SnowfallBackground background={background} />
      <MenuBar onBackgroundChange={setBackground} />
      <div className="main-content" style={styles.mainContent}>
        <SequenceWidget />
        <OptionPicker />
      </div>
    </div>
  );
};

export default MainWidget;
