import React, { useEffect, useState } from "react";
import GraphEditorPictographContainer from "./GraphEditorPictographContainer";
import TurnsBox from "./TurnsBox/TurnsBox";

const GraphEditor = ({ isExpanded }) => {
  const [isFullyCollapsed, setIsFullyCollapsed] = useState(false);

  useEffect(() => {
    if (!isExpanded) {
      const timer = setTimeout(() => setIsFullyCollapsed(true), 300); // Match animation duration
      return () => clearTimeout(timer);
    } else {
      setIsFullyCollapsed(false);
    }
  }, [isExpanded]);

  const styles = {
    graphEditor: {
      position: "relative",
      backgroundColor: "#f4f4f4",
      borderTop: "2px solid #ccc",
      overflow: "hidden",
      width: "100%",
      height: isExpanded ? "300px" : "0px",
      opacity: isFullyCollapsed ? 0 : 1,
      transition: "height 0.3s ease-in-out, opacity 0.3s ease-in-out",
    },
    mainLayout: {
      display: "flex",
      flexDirection: "row",
      height: "100%",
    },
    turnsBox: {
      flex: 1,
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
    },
    pictographContainer: {
      flex: "0 0 auto",
      width: "auto",
      height: "100%",
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
    },
  };

  return (
    <div style={styles.graphEditor}>
      <div style={styles.mainLayout}>
        <div style={styles.turnsBox}>
          <TurnsBox color="blue" label="Left" />
        </div>
        <div style={styles.pictographContainer}>
          <GraphEditorPictographContainer />
        </div>
        <div style={styles.turnsBox}>
          <TurnsBox color="red" label="Right" />
        </div>
      </div>
    </div>
  );
};

export default GraphEditor;
