import React from "react";

const GraphEditorToggleTab = ({ isExpanded, onToggle, duration }) => {
  const styles = {
    toggleTab: {
      position: "absolute",
      bottom: isExpanded ? "300px" : "0px", // Position dynamically
      left: "0",
      width: "100%",
      textAlign: "center",
      padding: "10px 20px",
      background: "linear-gradient(135deg, #6a11cb, #2575fc)",
      color: "white",
      fontSize: "1rem",
      fontWeight: "bold",
      cursor: "pointer",
      borderRadius: "10px 10px 0 0",
      border: "1px solid #555",
      transition: `bottom ${duration}ms ease-in-out, transform 0.3s ease-in-out`,
    },
    icon: {
      display: "inline-block",
      transform: isExpanded ? "rotate(180deg)" : "rotate(0deg)",
      transition: `transform ${duration}ms ease-in-out`,
    },
  };

  return (
    <div style={styles.toggleTab} onClick={onToggle}>
      <span style={styles.icon}>{isExpanded ? "▲" : "▼"}</span>{" "}
      {isExpanded ? "Collapse Editor" : "Expand Editor"}
    </div>
  );
};

export default GraphEditorToggleTab;
