import React from "react";

const GraphEditorToggleTab = ({ onToggle, isExpanded, position }) => {
  const styles = {
    toggleTab: {
      position: "absolute",
      bottom: "0", // Always aligned at the bottom of the container
      left: "0", // Align to the left
      width: "20%", 
      textAlign: "center",
      padding: "5px",
      backgroundColor: "#ccc",
      cursor: "pointer",
      fontSize: "1rem",
      border: "1px solid #aaa",
      transition: "background-color 0.2s ease-in-out, transform 0.3s ease-in-out",
    },
    hover: {
      backgroundColor: "#ddd",
    },
  };

  return (
    <div
      style={{
        ...styles.toggleTab,
        transform: `translateY(${position}px)`, // Dynamically position tab
      }}
      onClick={onToggle}
      onMouseEnter={(e) =>
        (e.currentTarget.style.backgroundColor = styles.hover.backgroundColor)
      }
      onMouseLeave={(e) =>
        (e.currentTarget.style.backgroundColor = styles.toggleTab.backgroundColor)
      }
    >
      <p>{isExpanded ? "Collapse Editor" : "Expand Editor"}</p>
    </div>
  );
};

export default GraphEditorToggleTab;
