import React, { useRef, useEffect } from "react";
import SnowflakeManager from "./SnowflakeManager";

const SnowfallBackground = () => {
  const canvasRef = useRef(null);
  const snowflakeManager = useRef(new SnowflakeManager());
  const prevCanvasSize = useRef({ width: window.innerWidth, height: window.innerHeight });

  const animate = () => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");
    const width = canvas.width;
    const height = canvas.height;

    ctx.clearRect(0, 0, width, height);

    // Draw gradient background
    const gradient = ctx.createLinearGradient(0, 0, 0, height);
    gradient.addColorStop(0, "#142030");
    gradient.addColorStop(1, "#325078");
    ctx.fillStyle = gradient;
    ctx.fillRect(0, 0, width, height);

    // Draw and animate snowflakes
    snowflakeManager.current.draw(ctx, width, height);

    requestAnimationFrame(animate);
  };

  const handleResize = () => {
    const canvas = canvasRef.current;
    const { width: prevWidth, height: prevHeight } = prevCanvasSize.current;
    const widthScale = window.innerWidth / prevWidth;
    const heightScale = window.innerHeight / prevHeight;

    // Update canvas size
    canvas.width = window.innerWidth;
    canvas.height = window.innerHeight;
    prevCanvasSize.current = { width: canvas.width, height: canvas.height };

    // Adjust snowflake positions
    snowflakeManager.current.adjustPositions(widthScale, heightScale);
  };

  useEffect(() => {
    const canvas = canvasRef.current;

    // Initialize canvas size
    canvas.width = window.innerWidth;
    canvas.height = window.innerHeight;

    // Initialize snowflakes
    snowflakeManager.current.initialize(canvas.width, canvas.height, 100);

    // Start animation
    requestAnimationFrame(animate);

    // Add resize listener
    window.addEventListener("resize", handleResize);

    return () => {
      window.removeEventListener("resize", handleResize);
    };
  }, []);

  return <canvas ref={canvasRef} style={{ position: "absolute", top: 0, left: 0, width: "100%", height: "100%" }} />;
};

export default SnowfallBackground;
