import React, { useState } from "react";

const DirectSetTurnsDialog = ({ currentTurns, onSelectTurns, onClose, color }) => {
  const turnsValues = ["fl", "0", "0.5", "1", "1.5", "2", "2.5", "3"];
  const borderColor = color === "blue" ? "#2e3192" : "#ed1c24";

  const styles = {
    dialog: {
      position: "absolute",
      border: `4px solid ${borderColor}`,
      borderRadius: "10px",
      backgroundColor: "#ffffff",
      display: "grid",
      gridTemplateColumns: "repeat(4, 1fr)", // Grid layout for buttons
      gap: "15px",
      padding: "20px",
      boxShadow: "0px 4px 8px rgba(0, 0, 0, 0.2)",
      fontSize: "18px",
    },
    button: {
      border: `2px solid ${borderColor}`,
      borderRadius: "50%",
      width: "60px",
      height: "60px",
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
      backgroundColor: "white",
      cursor: "pointer",
      fontSize: "16px",
      fontWeight: "bold",
      transition: "background-color 0.3s, transform 0.2s, box-shadow 0.2s",
    },
    buttonHover: {
      backgroundColor: "#e0e7ff",
      boxShadow: "0px 4px 8px rgba(0, 0, 0, 0.1)",
      transform: "scale(1.1)",
    },
    buttonActive: {
      backgroundColor: "#c7d2fe",
      boxShadow: "inset 0px 4px 8px rgba(0, 0, 0, 0.2)",
    },
  };

  const [hoveredButton, setHoveredButton] = useState(null);
  const [clickedButton, setClickedButton] = useState(null);

  return (
    <div style={styles.dialog}>
      {turnsValues.map((value) => (
        <button
          key={value}
          style={{
            ...styles.button,
            ...(hoveredButton === value ? styles.buttonHover : {}),
            ...(clickedButton === value ? styles.buttonActive : {}),
          }}
          onMouseEnter={() => setHoveredButton(value)}
          onMouseLeave={() => setHoveredButton(null)}
          onMouseDown={() => setClickedButton(value)}
          onMouseUp={() => setClickedButton(null)}
          onClick={() => {
            onSelectTurns(value);
            onClose();
          }}
        >
          {value}
        </button>
      ))}
    </div>
  );
};

export default DirectSetTurnsDialog;
