import React, { useEffect, useRef, useState } from "react";
import GraphEditorPictographContainer from "./GraphEditorPictographContainer";
import TurnsBox from "./TurnsBox/TurnsBox";
import GraphEditorToggleTab from "./GraphEditorToggleTab";

const ANIMATION_DURATION = 300; // Shared duration in milliseconds

const GraphEditor = ({ isExpanded, onToggle }) => {
  const [isFullyCollapsed, setIsFullyCollapsed] = useState(false);
  const [editorHeight, setEditorHeight] = useState(0);
  const editorRef = useRef(null);

  useEffect(() => {
    if (!isExpanded) {
      const timer = setTimeout(() => setIsFullyCollapsed(true), ANIMATION_DURATION);
      return () => clearTimeout(timer);
    } else {
      setIsFullyCollapsed(false);
    }
  }, [isExpanded]);

  useEffect(() => {
    if (editorRef.current) {
      const resizeObserver = new ResizeObserver(() => {
        setEditorHeight(editorRef.current.offsetHeight);
      });
      resizeObserver.observe(editorRef.current);
      return () => resizeObserver.disconnect();
    }
  }, []);

  const styles = {
    graphEditor: {
      position: "relative",
      backgroundColor: "#f4f4f4",
      borderTop: "2px solid #ccc",
      overflow: "hidden",
      width: "100%",
      height: isExpanded ? "300px" : "0px", // Adjust height dynamically
      opacity: isFullyCollapsed ? 0 : 1,
      transition: `height ${ANIMATION_DURATION}ms ease-in-out, opacity ${ANIMATION_DURATION}ms ease-in-out`,
    },
    mainLayout: {
      display: "flex",
      flexDirection: "row",
      alignItems: "stretch",
      height: "100%",
    },
    turnsBox: {
      flex: 1,
      display: "flex",
    },
  };

  return (
    <div style={styles.graphEditor} ref={editorRef}>
      <div style={styles.mainLayout}>
        <div style={styles.turnsBox}>
          <TurnsBox color="blue" />
        </div>
        <GraphEditorPictographContainer height={editorHeight} />
        <div style={styles.turnsBox}>
          <TurnsBox color="red" />
        </div>
      </div>
      {/* Pass down `isExpanded` and `ANIMATION_DURATION` to the toggle tab */}
      <GraphEditorToggleTab
        isExpanded={isExpanded}
        position={isExpanded ? 0 : -300}
        onToggle={onToggle}
        animationDuration={ANIMATION_DURATION}
      />
    </div>
  );
};

export default GraphEditor;
