import React from "react";

const GraphEditorToggleTab = ({ isExpanded, onToggle, height, duration }) => {
  const styles = {
    toggleTab: {
      position: "absolute",
      bottom: `${height}px`, // Dynamically position based on height
      left: "0",
      width: "20%", // Explicit width to avoid full stretch
      textAlign: "center",
      padding: "10px 20px",
      background: "linear-gradient(135deg, #6a11cb, #2575fc)",
      color: "white",
      fontSize: "1rem",
      fontWeight: "bold",
      cursor: "pointer",
      borderRadius: "10px 10px 0 0",
      border: "1px solid #555",
      transition: `bottom ${duration}ms ease-in-out, transform ${duration}ms ease-in-out`,
      boxShadow: "0px 4px 8px rgba(0, 0, 0, 0.2)",
    },
    hover: {
      scale: "1.05",
      boxShadow: "0px 6px 12px rgba(0, 0, 0, 0.3)",
    },
    icon: {
      display: "inline-block",
      transform: isExpanded ? "rotate(180deg)" : "rotate(0deg)",
      transition: `transform ${duration}ms ease-in-out`,
    },
  };

  return (
    <div
      style={styles.toggleTab}
      onClick={onToggle}
      onMouseEnter={(e) => {
        e.currentTarget.style.transform = `scale(${styles.hover.scale})`;
        e.currentTarget.style.boxShadow = styles.hover.boxShadow;
      }}
      onMouseLeave={(e) => {
        e.currentTarget.style.transform = "scale(1)";
        e.currentTarget.style.boxShadow = styles.toggleTab.boxShadow;
      }}
    >
      <span style={styles.icon}>{isExpanded ? "▲" : "▼"}</span>{" "}
      {isExpanded ? "Collapse Editor" : "Expand Editor"}
    </div>
  );
};

export default GraphEditorToggleTab;
