import React from "react";

const DirectSetTurnsDialog = ({ currentTurns, onSelectTurns, onClose, color }) => {
  const turnsValues = ["fl", "0", "0.5", "1", "1.5", "2", "2.5", "3"];

  const dialogStyles = {
    position: "absolute",
    border: `2px solid ${color === "blue" ? "#2e3192" : "#ed1c24"}`,
    borderRadius: "5px",
    backgroundColor: "white",
    display: "flex",
    gap: "10px",
    padding: "10px",
    fontSize: "16px",
    boxShadow: "0px 4px 8px rgba(0, 0, 0, 0.2)", // Add some depth
  };

  const buttonStyles = {
    border: `2px solid ${color === "blue" ? "#2e3192" : "#ed1c24"}`,
    borderRadius: "50%",
    width: "50px", // Match size dynamically if needed
    height: "50px",
    backgroundColor: "white",
    fontSize: "1rem",
    fontWeight: "bold",
    cursor: "pointer",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    transition: "background-color 0.2s, transform 0.2s",
    boxShadow: "0px 2px 4px rgba(0, 0, 0, 0.1)",
  };

  const buttonHoverStyles = {
    backgroundColor: "#f0f0f0",
  };

  return (
    <div style={dialogStyles}>
      {turnsValues.map((value) => (
        <button
          key={value}
          style={buttonStyles}
          onMouseEnter={(e) => (e.currentTarget.style.backgroundColor = buttonHoverStyles.backgroundColor)}
          onMouseLeave={(e) => (e.currentTarget.style.backgroundColor = "white")}
          onClick={() => {
            onSelectTurns(value);
            onClose();
          }}
        >
          {value}
        </button>
      ))}
    </div>
  );
};

export default DirectSetTurnsDialog;
