import SequenceWidgetButton from "./SequenceWidgetButton";
import React, { useState, useEffect } from "react";

const SequenceWidgetButtonPanel = () => {
  const [buttonSize, setButtonSize] = useState(60); // Default size
  const buttons = [
    {
      icon: require("./icons/add_to_dictionary.png"),
      title: "Add to Dictionary",
    },
    { icon: require("./icons/save_image.png"), title: "Save Image" },
    { icon: require("./icons/eye.png"), title: "View Full Screen" },
    { icon: require("./icons/mirror.png"), title: "Mirror Sequence" },
    { icon: require("./icons/yinyang1.png"), title: "Swap Colors" },
    { icon: require("./icons/rotate.png"), title: "Rotate Sequence" },
    { icon: require("./icons/delete.png"), title: "Delete Beat" },
    { icon: require("./icons/clear.png"), title: "Clear Sequence" },
  ];

  useEffect(() => {
    const updateButtonSize = () => {
      const containerHeight = window.innerHeight;
      setButtonSize(Math.max(40, Math.min(80, containerHeight / 15)));
    };

    updateButtonSize();
    window.addEventListener("resize", updateButtonSize);
    return () => window.removeEventListener("resize", updateButtonSize);
  }, []);

  const buttonStyles = {
    width: `${buttonSize}px`,
    height: `${buttonSize}px`,
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    margin: "10px",
    borderRadius: "50%",
    border: "1px solid #ccc",
    backgroundColor: "white",
    cursor: "pointer",
    transition: "transform 0.2s",
  };

  return (
    <div>
      {buttons.map((button, index) => (
        <div key={index} style={buttonStyles}>
          <img
            src={button.icon}
            alt={button.title}
            style={{ width: "70%", height: "70%" }}
          />
        </div>
      ))}
    </div>
  );
};

export default SequenceWidgetButtonPanel;
