import React, { useEffect, useState } from "react";

const SequenceWidgetButtonPanel = ({
  onAddToDictionary,
  onSaveImage,
  onViewFullScreen,
  onMirrorSequence,
  onSwapColors,
  onRotateSequence,
  onDeleteBeat,
  onClearSequence,
}) => {
  const [buttonSize, setButtonSize] = useState(50); // Default size

  useEffect(() => {
    // Adjust button size based on the container size
    const updateButtonSize = () => {
      const containerHeight = window.innerHeight; // Or use a ref to the parent container
      const newSize = Math.max(40, Math.min(80, containerHeight / 20)); // Adjust scaling logic
      setButtonSize(newSize);
    };

    updateButtonSize(); // Set initial size
    window.addEventListener("resize", updateButtonSize); // Update on resize

    return () => {
      window.removeEventListener("resize", updateButtonSize); // Cleanup
    };
  }, []);

  const buttonPanelStyles = {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    gap: `${buttonSize * 0.2}px`, // Spacing scales with button size
    padding: "10px",
    backgroundColor: "transparent",
    height: "100%",
    width: "100%",
  };

  const buttonStyles = {
    width: `${buttonSize}px`,
    height: `${buttonSize}px`,
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    backgroundColor: "white",
    borderRadius: "50%",
    cursor: "pointer",
    transition: "background-color 0.2s",
    border: "1px solid #ccc",
    fontSize: `${buttonSize * 0.5}px`, // Adjust icon size
  };

  return (
    <div style={buttonPanelStyles}>
      {/* Group 1: Basic Tools */}
      <button
        style={buttonStyles}
        onClick={onAddToDictionary}
        title="Add to Dictionary"
      >
        üìñ
      </button>
      <button style={buttonStyles} onClick={onSaveImage} title="Save Image">
        üíæ
      </button>
      <button
        style={buttonStyles}
        onClick={onViewFullScreen}
        title="View Full Screen"
      >
        üëÅÔ∏è
      </button>

      {/* Spacer */}
      <div style={{ height: `${buttonSize * 0.5}px` }}></div>

      {/* Group 2: Transform Tools */}
      <button
        style={buttonStyles}
        onClick={onMirrorSequence}
        title="Mirror Sequence"
      >
        üîÑ
      </button>
      <button style={buttonStyles} onClick={onSwapColors} title="Swap Colors">
        ‚ö´‚ö™
      </button>
      <button
        style={buttonStyles}
        onClick={onRotateSequence}
        title="Rotate Sequence"
      >
        ‚ü≥
      </button>

      {/* Spacer */}
      <div style={{ height: `${buttonSize * 0.5}px` }}></div>

      {/* Group 3: Sequence Management */}
      <button style={buttonStyles} onClick={onDeleteBeat} title="Delete Beat">
        üóëÔ∏è
      </button>
      <button
        style={buttonStyles}
        onClick={onClearSequence}
        title="Clear Sequence"
      >
        ‚ùå
      </button>
    </div>
  );
};

export default SequenceWidgetButtonPanel;
